document.querySelectorAll(".magic-loading").forEach(e=>{const t=()=>{e.classList.add("ml-loaded"),e.querySelector(".ml-loading").classList.add("d-none"),e.querySelector(".ml-error").classList.add("d-none"),e.querySelector(".ml-content").classList.remove("d-none")},n=t=>{e.querySelector(".ml-error p").textContent=t,e.querySelector(".ml-loading").classList.add("d-none"),e.querySelector(".ml-error").classList.remove("d-none")};e.addEventListener("ml-show",t),e.addEventListener("ml-error",e=>n(e.detail.message)),e.querySelector(".ml-content").children.length>0&&t()})